import React from 'react'
import NavProfileHome from '../NavProfileHome/index'
import MemberSectionOnMHP from '../MemberSectionOnMHP.js'
import FriendCard from "../../components/profileCard/FriendCard";
import Wrapper from "../../components/profileCard/Wrapper";
import Title from ".../../components/profileCard/Title";
import friends from "../../../friends.json";


// export default function ProfileHome(props){




//     return(

//         <>
//         <NavProfileHome/>
//     <MemberSectionOnMHP/>
//   </>
//     )
// }
class ProfileHome extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends
  };

  removeFriend = id => {
    // Filter this.state.friends for friends with an id not equal to the id being removed
    const friends = this.state.friends.filter(friend => friend.id !== id);
    // Set this.state.friends equal to the new friends array
    this.setState({ friends });
  };

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Title>Friends List</Title>
        {this.state.friends.map(friend => (
          <FriendCard
            removeFriend={this.removeFriend}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            occupation={friend.occupation}
            location={friend.location}
          />
        ))}
      </Wrapper>
    );
  }
}

export default ProfileHome;
